			purchasePayload += "\"OfferCode\" : null,"
		}
		purchasePayload += "\"Pricing\" : {"
	        if (curformatID == '17') //DigitalCode Json
	        {
	            purchasePayload += "\"InitialNight\" : 0,"
	            purchasePayload += "\"ExtraNight\" : 0,"
