		    		//testRunner.testCase.setPropertyValue("HasLoyaltyDiscount","0");
		    		nonLoyaltyProductId.add(curLoyaltyTitle);
		    		nonLoyaltyProductPrice.add(curLoyaltyPrice);
		    		//log.info ("Flag is 0");
		    	}
