	if ((PromoCode == "" || PromoCode == "null" || PromoCode == null))
	{
		testRunner.testCase.setPropertyValue("HasPromoDiscount","0");//NO Promo
	}
	else
	{
		testRunner.testCase.setPropertyValue("HasPromoDiscount","1");
