	else
	{
		loyaltyTitleList = rentalTitleIds.toString().replaceAll("\\[","").replaceAll("\\]","").split(",");
		loyaltyTitlePrice = rentalDiscPrice.toString().replaceAll("\\[","").replaceAll("\\]","").split(",");
