		def curTitleId = getNonLoyaltyProductId.take(1).toString().replace("[", "").replace("]", "");
		def curTitlePrice = getNonLoyaltyProductPrice.take(1).toString().replace("[", "").replace("]", "").toBigDecimal();

		promoPayload += "{";
