		{
		promoPayload += "\"Amount\": $curTitlePrice";
		}
		else
		{
			promoPayload += "\"Amount\": $promoCodeValue";
		}
		promoPayload += "}";


		def remainingNonLoyaltyProductId = getNonLoyaltyProductId.drop(1).toString().replace("[", "").replace("]", "");
